def dias_situacion(listado = []):
    policias=[]
    npolicias= 0
    noficiales= 0

    m= 0
    m5=0
    t=0
    n=0
    c=0
    b=0
    l=0
    ap=0
    n=0
    v=0
    om= 0
    on=0
    om5=0
    ot=0
    oc=0
    ob=0
    ol=0
    ofap=0
    on=0
    ov=0
    policias= {'Policias': 
                [
                    {                    
                    } 
                ]
                
            }
    oficiales={'Oficiales': 
                [
                    {                    
                    } 
                ]            
            }   



    for i in listado:
        #print(i)

        if(i[0]== '1'):
            npolicias +=1
            if(i[2] == 'M'):
                m+=1
                policias['Policias'][0]['Ma単ana'] = m
            if(i[2] == 'N'):
                n+=1
                policias['Policias'][0]['Noche'] = n
            if(i[2] == 'M2'):
                m5+=1
                policias['Policias'][0]['Ma単ana5-2'] = m5
            if(i[2] == 'T'):           
                t+=1
                policias['Policias'][0]['Tarde'] = t
            if(i[2] == 'CIF' or  i[2] == 'CBO' or  i[2] == 'CPA'):           
                c+=1
                policias['Policias'][0]['Convenio'] = c        
            if(i[2] == 'L'):           
                l+=1
                policias['Policias'][0]['Libre'] = l
            if(i[2] == 'AP' or i[2] == 'AP-'):           
                ap+=1
                policias['Policias'][0]['Asuntos propios'] = ap
            if(i[2] == 'V'):           
                v+=1
                policias['Policias'][0]['Vacaciones'] = v
            if(i[2] == 'B' or i[2] == 'AP-'):           
                b+=1
                policias['Policias'][0]['Baja'] = b

        if(i[0]== '2'):
            noficiales +=1
            if(i[2] == 'M'):
                om+=1
                oficiales['Oficiales'][0]['Ma単ana'] = om
            if(i[2] == 'T'):           
                ot+=1
                oficiales['Oficiales'][0]['Tarde'] = ot
            if(i[2] == 'N'):           
                on+=1
                oficiales['Oficiales'][0]['Noche'] = on
            if(i[2] == 'M2'):
                om5+=1
                oficiales['Oficiales'][0]['Ma単ana5-2'] = om5
            if(i[2] == 'CIF' or  i[2] == 'CBO' or  i[2] == 'CPA'):           
                oc+=1
                oficiales['Oficiales'][0]['Convenio'] = oc        
            if(i[2] == 'L'):           
                ol+=1
                oficiales['Oficiales'][0]['Libre'] = ol
            if(i[2] == 'AP' or i[2] == 'AP-'):           
                ofap+=1
                oficiales['Oficiales'][0]['Asuntos propios'] = ofap
            if(i[2] == 'V'):           
                ov+=1
                oficiales['Oficiales'][0]['Vacaciones'] = ov
            if(i[2] == 'B' or i[2] == 'AP-'):           
                ob+=1
                oficiales['Oficiales'][0]['Baja'] = ob
            
            plantilla =[]
            plantilla.append(policias)
            plantilla.append(oficiales)
            return plantilla
    
